import { Component, OnInit } from '@angular/core';
import {Observable} from 'rxjs';
import {select, Store} from '@ngrx/store';
import {Worker} from '../models/worker';
import {selectWorkers} from '../store/selectors';

@Component({
  selector: 'app-workers-list',
  templateUrl: './workers-list.component.html',
  styleUrls: ['./workers-list.component.css']
})
export class WorkersListComponent implements OnInit {

  private _workersList: Worker[] = [];

  constructor(private _store: Store<any>) { }

  ngOnInit() {
    this._store.select(selectWorkers).subscribe((workers) => {
      this._workersList = workers;
    });
  }

  get workersList(): Worker[] {
    return this._workersList;
  }

  set workersList(value: Worker[]) {
    this._workersList = value;
  }

  onWorkerClicked(): void {
    //send to api
  }
}
<div class="container">
  <p class="head-line">Workers</p>
  <button class="list" *ngFor="let worker of workersList" (click)="onWorkerClicked()">
    {{worker.name}}
  </button>
</div>
