import { Component, OnInit } from '@angular/core';
import {Observable} from 'rxjs';
import {select, Store} from '@ngrx/store';
import {Worker} from '../models/worker';

@Component({
  selector: 'app-workers-list',
  templateUrl: './workers-list.component.html',
  styleUrls: ['./workers-list.component.css']
})
export class WorkersListComponent implements OnInit {

  workersList: Worker[] = []
  // workersList: Worker[] = [
  //   new Worker(1, 'a'),
  //   new Worker(2, 'b'),
  //   new Worker(3, 'c'),
  //   new Worker(4, 'd'),
  //   new Worker(5, 'e')
  // ];

  workers$: Observable<Worker[]>;

  constructor(private store: Store<{workers: Worker[]}>) {
    this.workersList = store.pipe(select('workers'));
  }

  ngOnInit() {
  }

}
<div class="container">
  <p class="head-line">Workers</p>
  <div class="list" *ngFor="let worker of workersList">
    {{worker}}
  </div>
</div>
