import { Injectable } from '@angular/core';
import {Store} from '@ngrx/store';
import {SetFlights, SetWorkers} from '../store/actions';
import {Worker} from '../models/worker';
import {Flight} from '../models/flight';
import {FlightInformation} from '../models/flightInformation';
import {HttpClient} from '@angular/common/http';
import { Observable, throwError } from 'rxjs';
import { catchError, retry } from 'rxjs/operators';

@Injectable({
  providedIn: 'root'
})
export class DataReceiverService {

  constructor(private _store: Store<{workers: Worker[]}>, private _http: HttpClient) {
    this.getWorkers();
    this.getFlightsByWorkerId(1);
  }

  getWorkers() {
    // cycle
    // get data
    let workers: Worker[] = Workers;
    // return this._http.get<any>('http://localhost:3000/workers');
    // add to store
    this._store.dispatch(new SetWorkers({workers}));
  }

  getFlightsByWorkerId(workerId: number) {
    // delete old interval
    // create new interval
    // add to store
    let flights: Flight[] = Flights;
    this._store.dispatch(new SetFlights({flights}));
  }
}

const Workers: Worker[] = [
  new Worker(1, 'a'),
  new Worker(2, 'b'),
  new Worker(3, 'c'),
  new Worker(4, 'd'),
  new Worker(5, 'e')
];

const Flights: Flight[] = [
