import { createSelector } from '@ngrx/store';
import {Worker} from '../models/worker';

// export interface FeatureState {
//   workers: Worker[];
// }

export interface AppState {
  workers: Worker[];
}

export const selectFeature = (state: AppState) => state.workers;

export const selectWorkers = createSelector(selectFeature, (state: AppState) => state.workers);
