import {createFeatureSelector, createSelector} from '@ngrx/store';
import {Worker} from '../models/worker';

// export interface FeatureState {
//   workers: Worker[];
// }

export interface AppState {
  // feature: FeatureState;
  workers: Worker[];
}

export const getSpinnerState = createFeatureSelector<State>('spinner');

export const getSpinner = createSelector(
  getSpinnerState,
  (state: State) => state.isOn
);

// export const selectFeature = (state: AppState) => state.workers;

export const selectWorkers = createSelector((state: AppState) => state.workers, []);
