import {createFeatureSelector, createSelector} from '@ngrx/store';
import {AppState, FeatureState} from './app-state';
import {Worker} from '../models/worker';
import {Flight} from '../models/flight';
import {FlightInformation} from '../models/flightInformation';

// export const selectFeature = (state: AppState) => state.feature;
// export const selectFeature = createFeatureSelector<AppState, FeatureState>('feature');

export const selectUser = (state: AppState) => state.selectedUser;
export const selectAllBooks = (state: AppState) => state.allBooks;

export const workersFeature = (state: AppState) => state.workers;
// export const workersFeature = createFeatureSelector<AppState>('workers');
export const selectWorkers = createSelector(workersFeature, (state: FeatureState) => state.workers);

export const flightsFeature = (state: AppState) => state.flights;
// export const flightsFeature = createFeatureSelector<AppState>('flights');
export const selectFlights = createSelector(flightsFeature, (state: FeatureState) => state.flights);

export const flightInformationFeature = (state: AppState) => state.flightInformation;
// export const flightInformationFeature = createFeatureSelector<AppState>('flightInformation');
export const selectFlightInformation = createSelector(flightInformationFeature, (state: FeatureState) => state.flightInformation);
