import { Component, OnInit } from '@angular/core';
import {Flight} from '../models/flight';
import {Store} from '@ngrx/store';
import {selectFlights} from '../store/selectors';

@Component({
  selector: 'app-flights-table',
  templateUrl: './flights-table.component.html',
  styleUrls: ['./flights-table.component.css']
})
export class FlightsTableComponent implements OnInit {

  private _displayedColumns: string[] = ['flightNumber', 'origin', 'originDate', 'destination', 'destinationDate'];
  private _flightsList: Flight[] = [];

  constructor(private _store: Store<any>) { }

  ngOnInit() {
    this._store.select(selectFlights).subscribe((flights) => {
      this.flightsList = flights;
    });
  }

  get displayedColumns(): string[] {
    return this._displayedColumns;
  }

  set displayedColumns(value: string[]) {
    this._displayedColumns = value;
  }

  get flightsList(): any {
    return this._flightsList;
  }

  set flightsList(value: any) {
    this._flightsList = value;
  }
}
<table mat-table [dataSource]="flightsList" class="mat-elevation-z8">

  <ng-container matColumnDef="flightNumber">
    <th mat-header-cell *matHeaderCellDef> Flight Number </th>
    <td mat-cell *matCellDef="let element"> {{element.flightNumber}} </td>
  </ng-container>

  <ng-container matColumnDef="origin">
    <th mat-header-cell *matHeaderCellDef> Origin </th>
    <td mat-cell *matCellDef="let element"> {{element.origin}} </td>
  </ng-container>

  <ng-container matColumnDef="originDate">
    <th mat-header-cell *matHeaderCellDef> Origin Date </th>
    <td mat-cell *matCellDef="let element"> {{element.originDate | date: 'd/M/yyyy'}} </td>
  </ng-container>

  <ng-container matColumnDef="destination">
    <th mat-header-cell *matHeaderCellDef> Destination </th>
    <td mat-cell *matCellDef="let element"> {{element.destination}} </td>
  </ng-container>

  <ng-container matColumnDef="destinationDate">
    <th mat-header-cell *matHeaderCellDef> Destination Date </th>
    <td mat-cell *matCellDef="let element"> {{element.destinationDate | date: 'd/M/yyyy'}} </td>
  </ng-container>

  <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
  <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
</table>
