import {createFeatureSelector, createSelector} from '@ngrx/store';
import {AppState} from './app-state';
import {Worker} from '../models/worker';
import {Flight} from '../models/flight';
import {FlightInformation} from '../models/flightInformation';

export const getWorkersState = createFeatureSelector<AppState>('workers');
export const selectWorkers = createSelector(getWorkersState, (state: Worker[]) => state);

export const getFlightsState = createFeatureSelector<AppState>('flights');
export const selectFlights = createSelector(getFlightsState, (state: Flight[]) => state);

export const getFlightInformationState = createFeatureSelector<AppState>('flightInformation');
export const selectFlightInformation = createSelector(getFlightInformationState, (state: FlightInformation) => state);
